mapper.update:
update personal_data
user_id = #{userId}, name = #{name}, description = #{description}, category_id = #{categoryId}, file_link = #{fileLink}, status = #{status}, update_by = #{updateBy}, update_from = #{updateFrom}, update_time = #{updateTime},
where data_id = #{dataId}

mapper.select:
select * from personal_data

    and data_id = #{dataId}
    and user_id = #{userId}
    and name like concat('%', #{name}, '%')
    and description like concat('%', #{description}, '%')
    and category_id = #{categoryId}
    and status = #{status}

order by create_time desc
