mapper.update:
update personal_user
username = #{username}, password = #{password}, nickname = #{nickname}, email = #{email}, phone = #{phone}, gender = #{gender}, country_id = #{countryId}, nin = #{nin}, status = #{status}, wx_openid = #{wxOpenid}, update_by = #{updateBy}, update_from = #{updateFrom}, update_time = #{updateTime},
where user_id = #{userId}

mapper.select:
        select * from personal_user


                and user_id = #{userId}


                and username like concat('%', #{username}, '%')


                and nickname like concat('%', #{nickname}, '%')


                and email like concat('%', #{email}, '%')


#                 and phone like concat('%', #{phone}, '%')
                 and phone = #{phone}


                and gender = #{gender}


                and country_id = #{countryId}


                and nin like concat('%', #{nin}, '%')


                and status = #{status}


        order by create_time desc
