mapper.update:
update personal_user

    username = #{username}, 
    password = #{password}
    nickname = #{nickname}
    email = #{email}
    phone = #{phone}
    gender = #{gender}
    country_id = #{countryId}
    nin = #{nin}
    status = #{status}
    wx_openid = #{wxOpenid}
    update_by = #{updateBy}
    update_from = #{updateFrom}
    update_time = #{updateTime}

where user_id = #{userId}
